<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8">
    <title></title>
    <meta name="viewport"
          content="width=device-width,user-scalable=no,initial-scale=1,viewport-fit=cover,maximum-scale=1">
    <meta name="apple-mobile-web-app-capable" content="yes">
    <meta name="apple-mobile-web-app-status-bar-style" content="black">
    <style>
        * {
            padding: 0rem;
            margin: 0rem;
        }

        .header-box {
            /* width: 10rem; */
            /* height: 1.866666rem; */
            background: linear-gradient(-30deg, #FE4141, #F62C27);
            display: flex;
            padding: 0.96rem 0.44rem 0.346666rem 0.44rem;
        }

        .binding_btn {
            width: 8rem;
            height: 1.066666rem;
            background-color: #F62C27;
            margin: 1.146666rem auto;
            border-radius: 0.133333rem;
            display: flex;
            justify-content: center;
            align-items: center;
            color: #FFFFFF;
        }
    </style>
</head>
<body style="background-color: #FFFFFF;">
<div class="content-box">
    <div class="header-box">
        <div style="flex: 1;display: flex;align-items: center;">
            <!-- <img src="../../images/jxing.png" style="width: 0.2rem;height: 0.386666rem;" /> -->
        </div>
        <div
                style="flex: 1;font-size: 0.48rem;color: #FFFFFF;text-align: center;display: flex;justify-content: center;">
            绑定支付宝
        </div>
        <div style="flex: 1;"></div>
    </div>
    @if($auth_status)
        <div class="contentts" style="text-align: center;margin-top: 2.306666rem;">
            <img class="img1" src="" style="width: 2.133333rem;height: 2.133333rem;"/>
            <div
                    style="font-size: 0.4rem;margin-top: 0.426666rem;display: flex;justify-content: center;align-items: center;">
                <img id="success" src="" style="width: 0.426666rem;height: 0.426666rem;margin-right: 0.2rem;"/>
                支付宝绑定成功
            </div>
            <div
                    style="font-size: 0.346666rem;margin-top: 0.426666rem;display: flex;justify-content: center;align-items: center;color: #666666;">
                请返回来客APP
            </div>
        </div>
    @else
        <div class="contentts" style="text-align: center;margin-top: 2.306666rem;">
            <img class="img1" src="" style="width: 2.133333rem;height: 2.133333rem;"/>
            <div
                    style="font-size: 0.4rem;margin-top: 0.426666rem;display: flex;justify-content: center;align-items: center;">
                <img id="error" src='' style="width: 0.426666rem;height: 0.426666rem;margin-right: 0.2rem;"/>
                支付宝绑定失败
            </div>
            <div
                    style="font-size: 0.346666rem;margin-top: 0.426666rem;display: flex;justify-content: center;align-items: center;color: #666666;">
                请返回来客APP重新绑定
            </div>
        </div>

    @endif
</div>
</body>
<script>
    window.onload = function () {
        var Root = document.documentElement;
        Root.style.fontSize = window.innerWidth / 10 + "px";
    }
    let aplipay =
        'data:image/png;base64,';
    let error =
        'data:image/png;base64,'
    let success =
        'data:image/png;base64,'
    var img = document.getElementsByClassName('img1');
    [...img].forEach((items) => {
        items.src = aplipay
    });
    if (document.getElementById('success')) {
        document.getElementById('success').src = success;
    }
    if (document.getElementById('error')) {
        document.getElementById('error').src = error;
    }
</script>
</html>
